var dataarray = [];
for(var i=0; i<msg.payload.length; i++) {
    dataarray[i] = [];
}

var alLeastOne = false;

var series = []
var idx_data = 0;
for (let channel of msg.payload) {
    series.push(channel.channel.name)

    // find field name for ..
    var fieldName = "";
    if (channel.channel) {
        for (i = 1; i < 9 && fieldName == ""; i++) {
            if (channel.channel.hasOwnProperty("field"+i) && channel.channel["field"+i] == "temperature") {
                fieldName = "field"+i;
            }
        }
    }

    //skip management
    maxChartItems = flow.get("maxChartItems");
    if (!maxChartItems) { maxChartItems = 10;}
    skipEvery = parseInt(channel.feeds.length/maxChartItems) + 1;
    var i = 0;

	var a = 0;
    for (let feed of channel.feeds) {
        if (feed.hasOwnProperty(fieldName) && i++ % skipEvery == 0) {
            var d = new Date(feed.created_at);
            dataarray[idx_data][a++] = {"y": Number(feed[fieldName]), "x" : d.getTime()};
            alLeastOne = true;
        }
    }
    idx_data++;
}

var chart = [{
    "series": series,
    "data":dataarray,
    "labels": series
}];
flow.set("temperatureArray", chart);
msg.payload = chart;

if(!alLeastOne) {
    msg.enabled = false;
}
else {
    msg.enabled = true;
}
return msg

